<?php\n// Retrieve filters from POST data\nif(isset($_POST['filters']) && !empty($_POST['filters'])) {\n    $filters = json_decode($_POST['filters'], true);\n\n    // Initialize SQL statement\n    $sql = "SELECT * FROM your_table WHERE 1";\n\n    // Iterate through filters and build SQL query\n    foreach ($filters as $filter) {\n        if (isset($filter['field']) && isset($filter['value'])) {\n            $fieldName = $filter['field'];\n            $fieldValue = $filter['value'];\n            \n            // Sanitize inputs to prevent SQL injection\n            $sanitizedField = mysqli_real_escape_string($connection, $fieldName);\n            $sanitizedValue = mysqli_real_escape_string($connection, $fieldValue);\n\n            // Append filter to SQL statement\n            $sql .= " AND {$sanitizedField} LIKE '%{$sanitizedValue}%'";\n        }\n    }\n\n    // Execute query and fetch results\n    if ($result = mysqli_query($connection, $sql)) {\n        // Process the result...\n    } else {\n        echo json_encode(['error' => mysqli_error($connection)]);\n    }\n} else {\n    echo json_encode(['error' => 'No filters provided']);\n}\n?>