<?php\n// Your HTML content retrieved via cURL\n$htmlContent = '<html>\n  <body>\n    ..........\n    <div id="image">\n      <a href="somelocation">\n         <img src="location.jpg"/> <!-- I need to grab the src of this image object -->\n      </a>\n     </div>\n     <div> Other stuff blah blah</div>\n  </body>    \n</html>';\n\n// Create a new DOMDocument object\n$dom = new DOMDocument();\n\n// Load HTML into the DOMDocument object\n@$dom->loadHTML($htmlContent); // @ suppresses warnings for malformed HTML\n\n// Get the div with id "image"\n$imageDiv = $dom->getElementById('image');\n\nif ($imageDiv) {\n    // Find the img tag within the div\n    $imgTags = $imageDiv->getElementsByTagName('img');\n    if ($imgTags->length > 0) {\n        // Get the src attribute of the first img tag found\n        $srcValue = $imgTags->item(0)->getAttribute('src');\n        echo "The src attribute value is: $srcValue";\n    } else {\n        echo "No image found within the div.";\n    }\n} else {\n    echo "Div with id 'image' not found.";\n}\n?>