<?php\n// Load the XML file into SimpleXML\n$xml = simplexml_load_file('yourfile.xml');\n\n// Initialize an empty string for the select options\n$options = '';\n\n// Iterate through each <album> in the XML\nforeach ($xml->collection->children() as $item) {\n    if (isset($item->album)) {\n        foreach ($item->album as $album) {\n            // Check if 'contentnumber' exists\n            if (isset($album->contentnumber)) {\n                // Append an <option> element to the select menu\n                $options .= '<option value="' . htmlspecialchars((string)$album->contentnumber) . '">' .\n                            htmlspecialchars((string)$album->title) .\n                            '</option>';\n            }\n        }\n    } elseif ($item instanceof SimpleXMLElement && $item->getName() == 'album' && isset($item->contentnumber)) {\n        // In case album is directly under the main collection\n        $options .= '<option value="' . htmlspecialchars((string)$item->contentnumber) . '">' .\n                    htmlspecialchars((string)$item->title) .\n                    '</option>';\n    }\n}\n\n// Generate the final select menu HTML\necho '<select name="select" id="select">';\necho $options;\necho '</select>';\n?>