// Load the image with alpha support\n$inputImage = imagecreatefrompng('input.png');\n\n// Set the image transparency handling if not already set\nimagealphablending($inputImage, false);\nimagesavealpha($inputImage, true);\n\n// Define selection coordinates (left, top, right, bottom)\n$selectionX1 = 50;\n$selectionY1 = 50;\n$selectionX2 = 150;\n$selectionY2 = 150;\n\n// Get the size of the selected area\n$width = $selectionX2 - $selectionX1;\n$height = $selectionY2 - $selectionY1;\n\n// Create a transparent color with alpha set to 0 (fully transparent)\n$transparentColor = imagecolorallocatealpha($inputImage, 255, 255, 255, 127); // 127 is fully transparent\n\n// Fill the selection area with the transparent color\nimagefilledrectangle($inputImage, $selectionX1, $selectionY1, $selectionX2, $selectionY2, $transparentColor);\n\n// Save or output the modified image\nimagepng($inputImage, 'output.png');\n\n// Free memory\nimagedestroy($inputImage);