<?php\n\nrequire 'vendor/autoload.php';\n\nuse PhpOffice\PhpSpreadsheet\IOFactory;\nuse PhpOffice\PhpSpreadsheet\Html;\n\n// Create a new spreadsheet.\n$spreadsheet = IOFactory::createSpreadsheet();\n$sheetIndex = 0;\n\n// Array of your HTML file paths or content.\n$htmlFiles = [\n    'report1.html', // Path to first report\n    'report2.html', // Path to second report\n];\n\nforeach ($htmlFiles as $filename) {\n    if (!file_exists($filename)) {\n        echo "File not found: {$filename}\n";\n        continue;\n    }\n\n    $loadedSheet = Html::load($filename)->getActiveSheet();\n\n    // If this is not the first sheet, add a new sheet and assign it.\n    if ($sheetIndex != 0) {\n        $spreadsheet->createSheet();\n    }\n    \n    $newSheet = $spreadsheet->getSheet($sheetIndex);\n    $newSheet->setTitle("Report" . ($sheetIndex + 1));\n\n    // Copy loaded sheet content to the new sheet\n    $maxColumn = $loadedSheet->getHighestDataColumn();\n    $maxRow = $loadedSheet->getHighestDataRow();\n\n    for ($row = 1; $row <= $maxRow; $row++) {\n        for ($col = 'A'; $col <= $maxColumn; $col++) {\n            $value = $loadedSheet->getCell("$col$row")->getValue();\n            $newSheet->setCellValue("$col$row", $value);\n        }\n    }\n\n    $sheetIndex++;\n}\n\n// Save the file with multiple tabs.\n$writer = IOFactory::createWriter($spreadsheet, 'Xlsx');\n$writer->save('MultiTabReport.xlsx');\n\necho "Excel file created successfully.\n";\n\n?>