function latLongToXY($latitude, $longitude, $width, $height) {\n    // Assuming the world wraps around 360 degrees in both directions and centered on equator\n    $x = ($longitude + 180) / 360 * $width;\n    $y = (90 - $latitude) / 180 * $height;\n\n    return ['x' => $x, 'y' => $y];\n}\n\n$latitude = 40.7560540;\n$longitude = -73.9869510;\n$width = 1000;\n$height = 446;\n$point = latLongToXY($latitude, $longitude, $width, $height);\n\necho sprintf("X: %.2f, Y: %.2f", $point['x'], $point['y']);