<?php\n\nfunction getDirectorySize($directory) {\n    $size = 0;\n    foreach (glob(rtrim($directory, '/').'/*') as $item) {\n        if (is_dir($item)) {\n            $size += getDirectorySize($item);\n        } else {\n            $size += filesize($item);\n        }\n    }\n\n    return $size;\n}\n\nfunction formatSizeUnits($bytes)\n{\n    if ($bytes >= 1073741824) {\n        $bytes = number_format($bytes / 1073741824, 2) . ' GB';\n    } elseif ($bytes >= 1048576) {\n        $bytes = number_format($bytes / 1048576, 2) . ' MB';\n    } elseif ($bytes >= 1024) {\n        $bytes = number_format($bytes / 1024, 2) . ' KB';\n    } elseif ($bytes > 1) {\n        $bytes = $bytes . ' bytes';\n    } elseif ($bytes == 1) {\n        $bytes = $bytes . ' byte';\n    } else {\n        $bytes = '0 bytes';\n    }\n\n    return $bytes;\n}\n\n$mainDirectory = __DIR__; // Change this to your main folder path\n\necho "Directory sizes in '{$mainDirectory}':<br>";\nforeach (glob($mainDirectory.'/*', GLOB_ONLYDIR) as $dir) {\n    echo basename($dir) . ": " . formatSizeUnits(getDirectorySize($dir)) . "<br>";\n}\n?>