use BjyGrid\Exception\InvalidArgumentException;\n   use BjyGrid\Column;\n   use BjyGrid\DataSource\DoctrineORMQueryBuilder as DoctrineDataSource;\n   use BjyGrid\Filter;\n   use BjyGrid\Grid;\n\n   public function overviewAction()\n   {\n       $grid = new Grid(uniqid());\n\n       // Set up the data source\n       $entityManager = $this->getEntityManager();\n       $queryBuilder = $entityManager->getRepository('YourEntity')->createQueryBuilder('entity');\n       $dataSource = new DoctrineDataSource($queryBuilder);\n       $grid->setDataSource($dataSource);\n\n       // Setup grid columns and filters\n       try {\n           $grid->addColumns([\n               ['name' => 'id', 'title' => '#'],\n               ['name' => 'name', 'title' => 'Name', 'filterType' => Filter\Text::class],\n               ['name' => 'email', 'title' => 'Email', 'filterType' => Filter\Text::class],\n           ]);\n       } catch (InvalidArgumentException $ex) {\n           // Handle exceptions\n       }\n\n       return [\n           'grid' => $grid,\n       ];\n   }