<?php\ntry {\n    // Establishing connection (adjust parameters as needed)\n    $dbh = new PDO("mysql:host=localhost;dbname=your_database", "username", "password");\n    // Turning on error reporting (optional but recommended for debug)\n    $dbh->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Using prepared statement\n    $stmt = $dbh->prepare("SELECT * FROM table WHERE strSomeField = :somevalue");\n    $stmt->bindParam(':somevalue', $somevalue);\n    \n    // Execute the query\n    $stmt->execute();\n    $result = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\n    // Process result (iterate, use data, etc.)\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}\n?>