<?php\n   // Ensure ini settings are increased\n   ini_set('memory_limit', '128M');\n\n   if ($_SERVER['REQUEST_METHOD'] == 'POST' && isset($_FILES['myfile'])) {\n       $fileTmpPath = $_FILES['myfile']['tmp_name'];\n       $fileName = $_FILES['myfile']['name'];\n       $fileSize = $_FILES['myfile']['size'];\n       $fileType = $_FILES['myfile']['type'];\n\n       // Read file content\n       $fileContent = file_get_contents($fileTmpPath);\n\n       if ($fileContent === false) {\n           die('Error reading file.');\n       }\n\n       // Connect to MySQL\n       $pdo = new PDO("mysql:host=your_host;dbname=your_db", "your_user", "your_pass");\n       $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n       // Prepare SQL statement\n       $stmt = $pdo->prepare("INSERT INTO files (name, type, size, content) VALUES (:name, :type, :size, :content)");\n\n       // Bind parameters\n       $stmt->bindParam(':name', $fileName);\n       $stmt->bindParam(':type', $fileType);\n       $stmt->bindParam(':size', $fileSize);\n       $stmt->bindParam(':content', $fileContent, PDO::PARAM_LOB);\n\n       // Execute the statement\n       try {\n           $stmt->execute();\n           echo 'File uploaded successfully.';\n       } catch (PDOException $e) {\n           die('Database error: ' . $e->getMessage());\n       }\n   }\n   ?>