class Room extends AppModel {\n    public function isAvailable($roomId, $startDate, $endDate) {\n        $bookedRooms = $this->find('all', [\n            'conditions' => [\n                'Room.id' => $roomId,\n                'OR' => [\n                    ['Booking.start_date <=' => $endDate, 'Booking.end_date >=' => $startDate]\n                ]\n            ],\n            'recursive' => 0\n        ]);\n        return empty($bookedRooms);\n    }\n}