class BooksController extends AppController {\n    public function checkAvailability($bookId) {\n        if ($this->Book->isAvailable($bookId)) {\n            $message = 'The book is available.';\n        } else {\n            $message = 'Sorry, the book is currently not available.';\n        }\n        $this->set(compact('message'));\n    }\n\n    public function bookRoom() {\n        if ($this->request->is('post')) {\n            $roomId = $this->request->data['room_id'];\n            $startDate = $this->request->data['start_date'];\n            $endDate = $this->request->data['end_date'];\n\n            if ($this->Room->isAvailable($roomId, $startDate, $endDate)) {\n                // Proceed with booking logic\n                $message = 'The room is available and has been booked.';\n            } else {\n                $message = 'Sorry, the room is not available for the selected dates.';\n            }\n            $this->set(compact('message'));\n        }\n    }\n}