<?php\n// independent variables\n$dbHost = "localhost";\n$dbPort = 5432;\n$dbName = "masi";\n$dbUser = "masi";\n$dbPassword = "123456";\n\n// Connection string\n$connString = "host=$dbHost port=$dbPort dbname=$dbName user=$dbUser password=$dbPassword";\n\n// Connect to database\n$dbconn = pg_connect($connString);\n\nif (!$dbconn) {\n    // Handle connection errors\n    echo "Database connection failed: " . pg_last_error($dbconn);\n    exit;\n}\n\n// Using prepared statements to prevent SQL injection\n$username = 'a';  // Example value, replace with actual logic to get data from form\n$email = 'a';     // Example value, replace with actual logic to get data from form\n$passhash_md5 = 'a';  // Example value, replace with actual logic to get data from form\n\n$sql = "SELECT username, passhash_md5, email \n        FROM users\n        WHERE username = $1\n          AND email = $2 \n          AND passhash_md5 = $3";\n\n$result = pg_query_params($dbconn, $sql, array($username, $email, $passhash_md5));\n\nif (!$result) {\n    echo "An error occurred: " . pg_last_error($dbconn);\n    exit;\n}\n\n// Process the result as needed\nwhile ($row = pg_fetch_assoc($result)) {\n    // Example of processing each row\n    print_r($row);\n}\n\n// Free result and disconnect\npg_free_result($result);\npg_close($dbconn);\n?>