<?php\n// Assuming $con is your MySQL connection object created with mysqli_connect()\n\n$jobno = $_POST['jobno'];\n$jname = $_POST['journal'];\n$date_acceptance = $_POST['acceptancedate'];\n$date_published = $_POST['publisheddate'];\n$comment = $_POST['comment'];\n\n// Prepare the statement\n$stmt = $con->prepare("UPDATE blpublication SET JournalName=?, AcceptanceDate=?, PublishedDate=?, Comment=? WHERE JobNo=?");\n\nif (false === $stmt) {\n    die('Error: ' . htmlspecialchars($con->error));\n}\n\n// Bind parameters\n$bind = $stmt->bind_param("sssss", $jname, $date_acceptance, $date_published, $comment, $jobno);\nif (false === $bind) {\n    die('Error: ' . htmlspecialchars($stmt->error));\n}\n\n// Execute the statement\n$execval = $stmt->execute();\nif (!$execval) {\n    die('Error: ' . htmlspecialchars($stmt->error));\n} else {\n    echo "Record updated successfully";\n}\n\n// Close the statement and connection\n$stmt->close();\n$con->close();\n?>