<?php\n$array = [];\n$value = 10;\n\n// Benchmarking $array[] = $value;\n$start = microtime(true);\nfor ($i = 0; $i < 1000000; $i++) {\n    $array[] = $value;\n}\n$end = microtime(true);\necho "Using \$array[]: " . ($end - $start) . " seconds\n";\n\n$array = []; // Reset the array for the next benchmark\n\n// Benchmarking array_push($array, $value);\n$start = microtime(true);\nfor ($i = 0; $i < 1000000; $i++) {\n    array_push($array, $value);\n}\n$end = microtime(true);\necho "Using array_push(): " . ($end - $start) . " seconds\n";\n?>