<?php\nrequire 'path/to/PHPMailerAutoload.php';\n\n$mail = new PHPMailer;\n\n$mail->isSMTP();\n$mail->Host = 'localhost';\n$mail->SMTPAuth = true;\n$mail->Username = 'smtp-username'; // Client's SMTP username\n$mail->Password = 'smtp-password'; // Client's SMTP password\n$mail->SMTPSecure = 'tls';        // Enable TLS encryption, `ssl` also accepted\n$mail->Port = 2525;               // TCP port to connect to\n\n// Recipients\n$mail->setFrom('you@example.com', 'Your Name');\n$mail->addAddress('recipient@example.com');     // Add a recipient\n\n// Attachments\n$mail->addAttachment('/path/to/file.pdf');         // Add attachments\n\n// Content\n$mail->isHTML(true);\n$mail->Subject = 'Here is the subject';\n$mail->Body    = 'This is the HTML message body <b>in bold!</b>';\n$mail->AltBody = 'This is the body in plain text for non-HTML mail clients';\n\nif(!$mail->send()) {\n    echo 'Message could not be sent.';\n    echo 'Mailer Error: ' . $mail->ErrorInfo;\n} else {\n    echo 'Message has been sent';\n}\n?>