<?php\n\n$dateFile = date("Y-m-d").".txt";\n\n// Open the file for reading and writing\n$file = fopen($dateFile, "r+") or exit("Unable to open file!");\n\nif ($_POST["lastname"] <> "") {\n    // Read the existing content\n    $existingContent = fread($file, filesize($dateFile));\n    \n    // Move the file pointer to the beginning of the file\n    fseek($file, 0);\n    \n    // Write new data followed by the existing content\n    fwrite($file, $_POST["lastname"] . "\n" . $existingContent);\n}\n\n// Truncate any extra data in case the new file is smaller than the old one\nftruncate($file, ftell($file));\n\nfclose($file);\n\n?>