<?php\nrequire(QCUBED_PROJECT_DIR . '/includes/ModelConnect.php');\n\nclass HelloWorldForm extends \QCubed\Page\FormBase {\n    protected $btnSayHello;\n    protected $lblMessage;\n\n    protected function formCreate() {\n        // Create a button, and set its click action to the SayHello method below\n        $this->btnSayHello = new \QCubed\Control\Button($this);\n        $this->btnSayHello->Text = 'Say Hello!';\n        $this->btnSayHello->AddAction(new \QCubed\Event\Click(), new \QCubed\Action\Ajax('SayHello'));\n\n        // Create a label that will display our friendly message\n        $this->lblMessage = new \QCubed\Control\Label($this);\n    }\n\n    protected function SayHello(\QCubed\Event\ParamsBase $params) {\n        $this->lblMessage->Text = 'Hello, World!';\n    }\n}