<?php\n\nnamespace MyProject\StringHelpers; // Use a unique prefix to avoid conflicts\n\nfunction match($string1, $string2): bool {\n    return strcasecmp(trim($string1), trim($string2)) === 0;\n}\n\n// Usage within the same file or another included file within the namespace\necho match("Hello", "hello");\n\n// To use from outside the namespace\necho \MyProject\StringHelpers\match("Hello", "hello");\n?>