<?php\n// List of your JavaScript files\n$jsFiles = [\n    'script1.js',\n    'script2.js',\n    'script3.js'\n];\n\nheader("Content-type: text/javascript");\nob_start("mb_output_handler");\n\n$output = '';\nforeach ($jsFiles as $file) {\n    if (file_exists($file)) {\n        $output .= file_get_contents($file);\n        // Optionally add a semicolon at the end to avoid syntax errors in case scripts are not terminated properly\n        $output .= ';';\n    }\n}\n\necho $output;\n\n// Output buffering\nob_end_flush();\n?>