class PersonsController extends AppController {\n\n    var $name = 'Persons';\n\n    public function add_comment($person_id) {\n        if (!empty($this->data)) {\n            // Set the person_id for the comment\n            $this->request->data['Comment']['person_id'] = $person_id;\n\n            // Save the data using saveAssociated or saveAll\n            if ($this->Person->saveAssociated($this->request->data)) {\n                $this->Session->setFlash('The comment was added successfully.');\n                $this->redirect(array('action' => 'view', $person_id));\n            } else {\n                $this->Session->setFlash('The comment could not be saved. Please, try again.');\n            }\n        }\n\n        // Fetch the person's data or any other setup you need for your view\n        $this->set('person', $this->Person->findById($person_id));\n    }\n}