<?php\n\n// Step 1: Generate XML content from database\n$xmlContent = '<?xml version="1.0" encoding="UTF-8"?>\n<data>\n    <item>Value1</item>\n    <item>Value2</item>\n    <!-- Add your database fetched data here -->\n</data>';\n\n$filename = 'data.xml.gz';\n\n// Step 2: Compress the XML content using gzip\n$gzData = gzencode($xmlContent, 9);\n\n// Step 3: Send appropriate headers to initiate download\nheader('Content-Type: application/octet-stream');\nheader("Content-Transfer-Encoding: Binary");\nheader("Content-disposition: attachment; filename=\"{$filename}\"");\nheader('Content-Length: ' . strlen($gzData));\n\n// Output the compressed data\necho $gzData;\nexit;\n\n?>