<?php\n\nuse PHPUnit\Framework\TestCase;\n\nclass DatabaseTest extends TestCase {\n    protected $pdo;\n\n    public function setUp(): void {\n        // Connect to the database and seed with test data\n        $this->pdo = new PDO('mysql:host=localhost;dbname=testDB;', 'username', 'password');\n        $statement = $this->pdo->prepare("INSERT INTO users (id, name) VALUES (:id, :name)");\n        $statement->bindParam(':id', $id);\n        $statement->bindParam(':name', $name);\n        $users = [\n            ['id' => 1, 'name' => 'Alice'],\n            ['id' => 2, 'name' => 'Bob']\n        ];\n        foreach ($users as $user) {\n            extract($user);\n            $statement->execute();\n        }\n    }\n\n    public function tearDown(): void {\n        // Clear the database\n        $this->pdo->exec("DELETE FROM users");\n    }\n\n    public function testUserRetrieval() {\n        $query = $this->pdo->query("SELECT * FROM users WHERE id = 1");\n        $user = $query->fetch();\n        $this->assertEquals('Alice', $user['name']);\n    }\n}