// Define a constant\ndefine('MAX_USERS', 100);\n\n// Define a variable\n$maxUsers = 100;\n\nfunction displayMaxUsers() {\n    // Accessing the constant inside a function\n    echo MAX_USERS; // Outputs: 100\n\n    // Accessing the variable requires global declaration\n    global $maxUsers;\n    echo $maxUsers; // Outputs: 100\n}\n\ndisplayMaxUsers();\n\n// Changing the variable's value\n$maxUsers = 200;\n\necho $maxUsers; // Outputs: 200\n\n// Trying to change the constant's value will result in an error\n// define('MAX_USERS', 200); // This will throw a warning and won't override the value