$xml = new SimpleXMLElement(file_get_contents("StopPointList.xml"));\n\nfunction searchBusStops(SimpleXMLElement $xml, $searchTerm) {\n    // Prepare the search term for case-insensitive search\n    $pattern = '/'. preg_quote($searchTerm, '/') .'/i';\n\n    foreach ($xml->StopPoint as $stop) {\n        if (preg_match($pattern, (string)$stop->StopName)) {\n            echo $stop->StopName . '<br />';\n        }\n    }\n}\n\n// Example usage: search for bus stops with "sen" in their name\n$searchTerm = 'sen';\nsearchBusStops($xml, $searchTerm);