<?php\n\ndefine('CACHE_FILE', 'tweets_cache.json');\ndefine('TWITTER_API_URL', 'https://api.twitter.com/1.1/statuses/user_timeline.json'); // Example endpoint\ndefine('CACHE_DURATION', 900); // Cache for 15 minutes (seconds)\ndefine('BEARER_TOKEN', 'your_twitter_bearer_token');\n\nfunction getTweetsFromAPI() {\n    $ch = curl_init();\n    curl_setopt_array($ch, [\n        CURLOPT_URL => TWITTER_API_URL . '?screen_name=twitter_handle&count=5',\n        CURLOPT_RETURNTRANSFER => true,\n        CURLOPT_HTTPHEADER => ['Authorization: Bearer ' . BEARER_TOKEN]\n    ]);\n\n    $tweets = json_decode(curl_exec($ch), true);\n    curl_close($ch);\n\n    return $tweets;\n}\n\nfunction saveTweetsToCache($tweets) {\n    file_put_contents(CACHE_FILE, json_encode($tweets));\n}\n\nfunction getTweets() {\n    if (file_exists(CACHE_FILE) && (time() - filemtime(CACHE_FILE) < CACHE_DURATION)) {\n        // Use cached version\n        $tweets = file_get_contents(CACHE_FILE);\n    } else {\n        // Fetch and cache new tweets\n        $tweets = getTweetsFromAPI();\n        saveTweetsToCache($tweets);\n\n        // Convert array to JSON string for storage in local file\n        $tweets = json_encode($tweets);\n    }\n\n    // Decode the JSON string back into an associative array for use\n    return json_decode($tweets, true);\n}\n\n// Display tweets on the homepage\n$tweets = getTweets();\nforeach ($tweets as $tweet) {\n    echo "<p>" . htmlspecialchars($tweet['text']) . "</p>";\n}