// Assume this is in your second script/page after user registration\nsession_start();\n\nif (isset($_POST['address'])) {\n    $address = $_POST['address'];\n    \n    // Insert residence\n    $sqlResidence = "INSERT INTO residences (address) VALUES (?)";\n    $stmtResidence = mysqli_prepare($conn, $sqlResidence);\n    mysqli_stmt_bind_param($stmtResidence, "s", $address);\n\n    if (mysqli_stmt_execute($stmtResidence)) {\n        // Get the last inserted ID for residences\n        $residence_id = mysqli_insert_id($conn);\n        \n        // Insert into users_residences\n        $sqlUserResidence = "INSERT INTO users_residences (user_id, residence_id) VALUES (?, ?)";\n        $stmtUserResidence = mysqli_prepare($conn, $sqlUserResidence);\n        $userId = $_SESSION['user_id'];\n        mysqli_stmt_bind_param($stmtUserResidence, "ii", $userId, $residence_id);\n        \n        if (!mysqli_stmt_execute($stmtUserResidence)) {\n            echo "Error inserting into users_residences: " . mysqli_error($conn);\n        }\n    } else {\n        echo "Error inserting residence: " . mysqli_error($conn);\n    }\n}