<?php\n\n// Replace with your own API key\n$apiKey = 'YOUR_API_KEY';\n\n// The search term you want to use\n$searchTerm = 'cats';\n\n// Google's base URL for making API calls\n$urlBase = "https://www.googleapis.com/youtube/v3/search";\n\n// Construct the query parameters\n$params = [\n    'q' => $searchTerm,\n    'key' => $apiKey,\n    'part' => 'snippet',\n    'type' => 'video',\n    'maxResults' => 5, // Number of results to return\n];\n\n// Build the full request URL\n$requestUrl = $urlBase . '?' . http_build_query($params);\n\n// Make the API call and get the response\n$response = file_get_contents($requestUrl);\n\n// Decode the JSON response\n$data = json_decode($response, true);\n\nif ($data && isset($data['items'])) {\n    // Loop through each video item in the result set\n    foreach ($data['items'] as $item) {\n        echo "Title: " . htmlspecialchars($item['snippet']['title']) . "\n";\n        echo "Video ID: " . htmlspecialchars($item['id']['videoId']) . "\n";\n        echo "Thumbnail URL: " . htmlspecialchars($item['snippet']['thumbnails']['default']['url']) . "\n\n";\n    }\n} else {\n    echo "No results found or an error occurred.";\n}\n\n?>