<?php\n// Database connection setup\n$mysqli = new mysqli("localhost", "user", "password", "database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Prepare the stored procedure call\n$stmt = $mysqli->prepare("CALL your_stored_procedure(?, @out_param)");\n\n// Bind input parameters if any (in this case none is shown)\n// For example, if you have an IN parameter:\n// $stmt->bind_param("i", $inputParam);\n\n// Execute the statement\n$stmt->execute();\n\n// Close the prepared statement after execution\n$stmt->close();\n\n// Now, fetch the OUT parameter value\n$result = $mysqli->query("SELECT @out_param AS outParam");\n$row = $result->fetch_assoc();\n\n$outValue = $row['outParam'];\necho "The OUT parameter value is: " . $outValue;\n\n// Clean up\n$mysqli->close();\n?>