$title = $fetch_content['article_name']; // example output "Hello World, Ask A Question"\n\n// Remove punctuation and split the title into words\n$keywords = preg_split('/[\s,]+/', trim($title));\n\n// Initialize SQL query with placeholders for each keyword\n$sql_keywords = array_fill(0, count($keywords), '%?%');\n$query = "SELECT * FROM tbl_content WHERE status='t' AND article_name LIKE " . implode(' OR article_name LIKE ', $sql_keywords) . " ORDER BY id DESC LIMIT 5";\n\n// Prepare and execute the query using PDO\ntry {\n    $pdo = new PDO('mysql:host=your_host;dbname=your_db', 'username', 'password');\n    $stmt = $pdo->prepare($query);\n    \n    // Bind each keyword to the placeholders\n    foreach ($keywords as $index => $keyword) {\n        $stmt->bindValue(($index + 1), '%' . $keyword . '%', PDO::PARAM_STR);\n    }\n    \n    // Execute and fetch results\n    $stmt->execute();\n    $relatedArticles = $stmt->fetchAll(PDO::FETCH_ASSOC);\n    \n    print_r($relatedArticles);\n} catch (PDOException $e) {\n    echo "Error: " . $e->getMessage();\n}