<?php\n$xmlContent = file_get_contents('your-rss-file.xml'); // replace with your file path\n\n$dom = new DOMDocument();\n@$dom->loadXML($xmlContent); // Suppress warnings for malformed XML\n\n$items = $dom->getElementsByTagName('item');\n\nforeach ($items as $item) {\n    $descriptionNodeList = $item->getElementsByTagName('description');\n    if ($descriptionNodeList->length > 0) {\n        $description = $descriptionNodeList->item(0)->nodeValue;\n        echo htmlspecialchars($description, ENT_QUOTES, 'UTF-8') . "<br>";\n    }\n}\n?>