<?php\n$string = "12,15,22";\n$ids = explode(',', $string);\n\ntry {\n    // Assuming you have a PDO instance named $pdo\n    // Replace 'host', 'dbname', 'user', and 'pass' with your database credentials\n    $pdo = new PDO('mysql:host=host;dbname=dbname', 'user', 'pass');\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Create a string of placeholders for the ids e.g., ?,?,?\n    $placeholders = implode(',', array_fill(0, count($ids), '?'));\n\n    // Prepare and execute the query\n    $query = "SELECT usrFirst, usrLast FROM tblusers WHERE usrID IN ($placeholders)";\n    $stmt = $pdo->prepare($query);\n    $stmt->execute($ids);\n\n    // Fetch results\n    while ($row = $stmt->fetch(PDO::FETCH_ASSOC)) {\n        echo "First Name: " . htmlspecialchars($row['usrFirst']) . ", Last Name: " . htmlspecialchars($row['usrLast']) . "<br>";\n    }\n\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}\n?>