// PHP 4 style\nclass User {\n    function User() { // acts as constructor\n        $this->name = "Guest";\n    }\n}\n\n// PHP 5 preferred style with destructor included for demonstration\nclass User {\n    public function __construct() { // improved way to define a constructor\n        $this->name = "Guest";\n    }\n\n    public function __destruct() { // destructor example\n        unset($this->name);\n    }\n}