class My_Controller_Plugin_ActionStackLogger extends Zend_Controller_Plugin_Abstract\n{\n    protected $stack = [];\n\n    public function preDispatch(Zend_Controller_Request_Abstract $request)\n    {\n        // Store the current action/controller in our internal stack.\n        $this->stack[] = [\n            'action' => $request->getActionName(),\n            'controller' => $request->getControllerName()\n        ];\n    }\n\n    // Method to get all actions that were pushed on the stack\n    public function getAllStackedActions()\n    {\n        return $this->stack;\n    }\n}