private function parse($tag, $attr = 'name')\n{\n    $strict = 0;\n    /*** the array to return ***/\n    $out = array();\n    if ($this->totalBlocks() > 0) {\n        /*** a new dom object ***/\n        $dom = new DOMDocument;\n        /*** discard white space ***/\n        $dom->preserveWhiteSpace = false;\n\n        /*** load the html into the object ***/\n        if ($strict == 1) {\n            $dom->loadXML($this->file_contents);\n        } else {\n            libxml_use_internal_errors(true); // Suppress HTML warnings\n            $dom->loadHTML($this->file_contents);\n            libxml_clear_errors();\n        }\n\n        /*** the tag by its tag name ***/\n        $content = $dom->getElementsByTagName($tag);\n\n        foreach ($content as $item) {\n            /*** add node value to the out array ***/\n            $out[] = [\n                'name' => $item->getAttribute($attr),\n                // Use saveXML to get the inner HTML including tags\n                'value' => trim($dom->saveXML($item->FirstChild))\n            ];\n        }\n    }\n\n    return $out;\n}