function uncompress($srcName, $dstName) {\n    $chunkSize = 4096; // Define chunk size (adjust as needed)\n\n    $source = gzopen($srcName, "rb");\n    if (!$source) {\n        throw new Exception("Failed to open source file.");\n    }\n\n    $destination = fopen($dstName, "wb");\n    if (!$destination) {\n        gzclose($source);\n        throw new Exception("Failed to open destination file.");\n    }\n\n    while (!gzeof($source)) {\n        $buffer = gzread($source, $chunkSize);\n        fwrite($destination, $buffer);\n    }\n\n    fclose($destination);\n    gzclose($source);\n}