<?php\nfunction hasUserVoted($gameId, $userId) {\n    global $conn; // assuming you have established a database connection stored in $conn\n    \n    $stmt = $conn->prepare("SELECT * FROM votes WHERE game_id = ? AND user_uid = ?");\n    $stmt->bind_param("ii", $gameId, $userId);\n    $stmt->execute();\n    return $stmt->fetch() !== null;\n}\n\nfunction recordVote($gameId, $userId) {\n    global $conn; // assuming you have established a database connection stored in $conn\n    \n    if (!hasUserVoted($gameId, $userId)) {\n        $stmt = $conn->prepare("INSERT INTO votes (game_id, user_uid) VALUES (?, ?)");\n        $stmt->bind_param("ii", $gameId, $userId);\n        return $stmt->execute();\n    }\n    return false; // User has already voted\n}\n?>