<?php\n\n$html = '...your html content here...';\n\n// Load the HTML into a DOMDocument object\n$dom = new DOMDocument();\n@$dom->loadHTML($html); // Suppress warnings with @\n\n// Create a DOMXPath object from the DOMDocument\n$xpath = new DOMXPath($dom);\n\n// Find all elements that contain <p> tags\n$query = '//*[count(.//p) > 0]';\n$elements = $xpath->query($query);\n\n$maxCount = 0;\n$maxElement = null;\n\nforeach ($elements as $element) {\n    // Count the number of <p> children in the current element\n    $pCount = $element->getElementsByTagName('p')->length;\n\n    if ($pCount > $maxCount) {\n        $maxCount = $pCount;\n        $maxElement = $element;\n    }\n}\n\nif ($maxElement !== null) {\n    echo "Element with most <p> tags: ", $dom->saveXML($maxElement);\n    echo "Number of <p> tags:", $maxCount;\n} else {\n    echo "No elements found containing <p> tags.";\n}\n?>