define('SITEPATH', (dirname($_SERVER['SCRIPT_NAME']) == '/') ? '/' : dirname($_SERVER['SCRIPT_NAME']).'/');\n\n$load_path = $_SERVER['DOCUMENT_ROOT'] . SITEPATH . 'fpo_image.jpg';\n\n$file_time  = filemtime($load_path);\n$last_modified = gmdate('D, d M Y H:i:s', $file_time) . ' GMT';\n$client_last_modified = isset($_SERVER['HTTP_IF_MODIFIED_SINCE']) ? $_SERVER['HTTP_IF_MODIFIED_SINCE'] : '';\n\nheader('Cache-Control: must-revalidate');\nheader("Last-Modified: {$last_modified}");\n\nif ($client_last_modified && strtotime($client_last_modified) >= $file_time) {\n    header('HTTP/1.1 304 Not Modified');\n} else {\n    header('HTTP/1.1 200 OK');\n    header('Content-Length: ' . filesize($load_path));\n    header('Content-type: image/jpeg');\n\n    readfile($load_path);\n}