require 'vendor/autoload.php'; // Make sure to include Firebase PHP-JWT library\n\nuse \Firebase\JWT\JWT;\n\n$key = "your-256-bit-secret";\n$payload = array(\n    "iss" => "http://www.example.com",\n    "aud" => "http://www.second-site.com",\n    "iat" => time(),\n    "nbf" => time() + 10,\n    "exp" => time() + 3600, // Token expires in 1 hour\n    "data" => array(\n        "userId" => 123\n    )\n);\n\n$jwt = JWT::encode($payload, $key, 'HS256');\necho "JWT: " . $jwt;