// Assume $cl is your SphinxClient instance configured for your index\n\n$searchQuery = "your search query here";\n$result = $cl->query($searchQuery);\n\nif ($result === false) {\n    echo "Query failed: " . $cl->GetLastError() . ". Warning: " . $cl->GetLastWarning();\n    exit;\n}\n\n// Fetch matches\n$matches = array_keys($result['matches']);\n\nif (!empty($matches)) {\n    // Prepare SQL query to fetch full results using document IDs from Sphinx\n    $idsList = implode(',', $matches);\n    $sqlQuery = "SELECT id, title, content FROM your_table WHERE id IN ($idsList) ORDER FIELD(id, $idsList)";\n\n    // Execute the query against your database\n    $pdo = new PDO('mysql:host=your_host;dbname=your_db', 'username', 'password');\n    $stmt = $pdo->query($sqlQuery);\n\n    // Fetch results\n    $rows = $stmt->fetchAll(PDO::FETCH_ASSOC);\n    \n    foreach ($rows as $row) {\n        echo "Title: " . $row['title'] . "\n";\n        echo "Content: " . $row['content'] . "\n\n";\n    }\n}