$text = '  <tr>\n        <td id="descriptionArea">\n            Customer request to remove "Intro - 01/13/09" video clip.\n            <br/>\n        </td>\n    </tr>';\n\n// Correct regex pattern with appropriate delimiters\n$pattern = '/<td id="descriptionArea">\s*(.*?)\s*<\/td>/s';\n\n// preg_match is the correct function to use here\npreg_match($pattern, $text, $matches);\n\nprint_r($matches[1]); // Output the captured group