class UserParameters {\n    public $code;\n    public $name;\n    public $time;\n    public $area;\n    public $domain;\n    public $text;\n    public $id;\n    public $num;\n    public $video;\n    public $audio;\n    public $image;\n\n    // You can also add a constructor here to set values if needed\n}\n\nclass User {\n    private $uCode;\n    private $uName;\n    private $uTime;\n    private $uArea;\n    private $uDomain;\n    private $uText;\n    private $uId;\n    private $uNum;\n    private $uVideo;\n    private $uAudio;\n    private $uImage;\n\n    public function __construct(UserParameters $params) {\n        $this->uCode = $params->code;\n        $this->uName = $params->name;\n        // and so on for all parameters...\n    }\n}\n\n// Usage\n$userParams = new UserParameters();\n$userParams->code = 'exampleCode';\n$userParams->name = 'exampleName';\n\n$user = new User($userParams);