class Mem {\n    private $delete = false;\n\n    function event($event) {\n        global $pdo;\n        global $memcached;\n\n        $key = md5(sha1($event) . sha1('events'));\n\n        if ($this->delete) {\n            $result = $memcached->delete($key);\n            // Reset delete flag after operation\n            $this->delete = false;\n            return $result;\n        } else {\n            return $memcached->get($key);\n        }\n    }\n\n    function delete() {\n        $this->delete = true;\n        // Return the current object instance to allow method chaining\n        return $this;\n    }\n}\n\n// Usage:\n$cache = new Mem();\n$result = $cache->event('your_event_here')->delete();  // This will delete the event cache