<?php\nrequire_once(LIBPATH . 'MySQLDB.php');\nrequire_once(LIBPATH . 'Authentication.php');\n\nclass Session {\n    public $url;\n    public $referrer;\n    public $redirect;\n    private $auth;\n\n    function __construct($authObject) {\n        $this->auth = $authObject; // Dependency injection\n        $this->startSession();\n    }\n\n    function startSession() {\n        session_start();\n\n        if (isset($_SESSION['url'])) {\n            $this->referrer = $_SESSION['url'];\n        } else {\n            $this->referrer = '/';\n        }\n        $this->url = $_SESSION['url'] = $_SERVER['PHP_SELF'];\n        if (isset($_SESSION['redirect'])) {\n            $this->redirect = $_SESSION['redirect'];\n        } else {\n            $this->redirect = $_SESSION['redirect'] = '/';\n        }\n\n        $this->auth->checkLogin(); // Use the injected object\n    }\n\n    function setRedirect($page) {\n        $this->redirect = $_SESSION['redirect'] = $page;\n    }\n}\n?>