use Zend_Date;\n\nfunction convertDateStringZend($dateString, $format) {\n    switch ($format) {\n        case 'mdy':\n            $zendDateFormat = 'MM/dd/yyyy';\n            break;\n        case 'dmy':\n            $zendDateFormat = 'dd/MM/yyyy';\n            break;\n        default:\n            throw new InvalidArgumentException("Unsupported format");\n    }\n\n    // Create Zend_Date object from string\n    $date = new Zend_Date($dateString, $zendDateFormat);\n\n    return $date->toString('yyyyMMdd');\n}\n\n// Example usage:\necho convertDateStringZend('12-25-2023', 'mdy'); // Outputs: 20231225\necho convertDateStringZend('25-12-2023', 'dmy'); // Outputs: 20231225