$page = intval($_GET['page']) ?? 0;\n$per = 10; // results per page\n$offset = $page * $per;\n\n// Count total number of records for pagination links\n$totalQuery = "SELECT COUNT(*) AS total FROM table WHERE conditions";\n$stmtTotal = $pdo->query($totalQuery);\n$totalCount = $stmtTotal->fetchColumn();\n$totalPages = ceil($totalCount / $per);\n\n// Fetch only the required rows\n$query = "SELECT columns FROM table WHERE conditions LIMIT :offset, :limit";\n$stmt = $pdo->prepare($query);\n$stmt->bindValue(':offset', $offset, PDO::PARAM_INT);\n$stmt->bindValue(':limit', $per, PDO::PARAM_INT);\n$stmt->execute();\n$resultset = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\n// Display navigation\nif ($page > 0) {\n    echo '<a href="?page='.($page-1).'">Previous</a> ';\n}\nif (($page + 1) < $totalPages) {\n    echo '<a href="?page='.($page+1).'">Next</a>';\n}\n\n// Display results\nforeach ($resultset as $row) {\n    // display each row\n}