preg_match('/^(Guo (\d+) Cars @(\w+)|AV (\d+) Cars @(\w+)|@(\w+) (\d+) [#]?av)(.*)/i', $source, $output);\n\n// Check which pattern matched by checking non-empty elements in the array\nif (isset($output[1])) {\n    // First or second pattern matched\n    if ($output[1] === 'Guo') {\n        echo "Pattern 1 matched: Source Text = Guo, Number Amount = {$output[2]}, Name = {$output[3]}";\n    } else {\n        echo "Pattern 2 matched: Source Text = AV, Number Amount = {$output[4]}, Name = {$output[5]}";\n    }\n} elseif (isset($output[6])) {\n    // Third pattern matched\n    echo "Pattern 3 matched: Source Text = @{$output[6]}, Number Amount = {$output[7]}, Name = Unknown in position 2 of output because of rearrangement.";\n}