function get_distinct_size_for_bracelets($pdo, $myvalue) {\n    // Prepare the SQL statement\n    $stmt = $pdo->prepare("SELECT DISTINCT size FROM mytable WHERE id = 27 AND type = :type ORDER BY size");\n    \n    // Bind the parameter to the value from the function argument\n    $stmt->bindParam(':type', $myvalue, PDO::PARAM_STR);\n    \n    // Execute the statement\n    $stmt->execute();\n    \n    // Fetch the results\n    return $stmt->fetchAll(PDO::FETCH_ASSOC);\n}\n\n// Example usage:\n$pdo = new PDO('mysql:host=your_host;dbname=your_dbname;charset=utf8', 'username', 'password');\n$myvalue = 'plastic';\n$sizes = get_distinct_size_for_bracelets($pdo, $myvalue);\n\nprint_r($sizes);