<?php\n// Make sure this file is being called from within a WordPress environment.\nif ( ! defined( 'ABSPATH' ) ) {\n    exit; // Exit if accessed directly\n}\n\n// Start the WordPress environment if you're in an external script\nrequire_once('/path/to/wordpress/wp-load.php');\n\n// Check if user is logged in and get their ID\n$user_id = get_current_user_id();\n\nif ( $user_id > 0 ) { // user is logged in\n    // Get user data\n    $user_info = get_userdata($user_id);\n\n    // Output username\n    echo "User Name: " . esc_html($user_info->user_login) . "\n";\n    echo "User ID: " . esc_html($user_id) . "\n";\n} else {\n    echo "No user is currently logged in.\n";\n}\n?>